import React, {Component} from 'react'
import AppHeader from '../app-header'
import AppSearch from '../app-search'
import AppList from '../app-list'
import './app.css'

export default class App extends Component {
  
  state = {
     prop : [
      {label: 'Drink coffee', important: false, id: 'nika'},
      {label: 'Make React App', important: true, id: 'pika'}
    ]
  };

  deleteItem = (id) => {
    this.setState(({prop}) => {
      
      const idx = prop.findIndex((el) => el.id === id);

      const newProp = [...prop.slice(0, idx), ...prop.slice(idx + 1)];

      return {
        prop:newProp
      };

    });
  };


  render() {
    return (
      <div className='app col-lg-4'>
          <AppHeader/>
          <AppSearch/>
          <AppList prop = {prop} onDeleted = {this.deleteItem}/>
      </div>    
    );
  };
    
    
  };
