import React, {Component} from 'react'
import './add-panel.css'

export default class AddPanel extends Component {

    state = {
        lable: ''
    };

    onLableChange = (e) => {
        this.setState({
            label: e.targer.value
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.props.onPanelAdd(this.state.label);
    }

    render() {

        const {onPanelAdd} = this.props;

        return (
            <form className = 'add-panel' onSubmit = {this.onSubmit}>
                <input type="text"
                onChange = {this.onLabelChange}
                />
