import React, {Component} from 'react'
import './app-list-item.css'

export default  class AppListItem extends Component {

    state = {
        done : false,
        important: false
    };

    onLabelClick = () => {
        this.setState({
            done: true
        });
    };

    onMarkImp = () => {
        this.setState({
            important: true
        });
    }


    render() {

        const {label} = this.props;
        const {done, important} = this.state;

       
        
        let classNames = 'list-group-item app-list-item d-flex justify-content-between';
        

        if(done) {
            classNames += 'done';
        }

        if(important) {
            classNames += ' important';
        }

        return (
            //list-group-item app-list-item d-flex justify-content-between
        <span className={classNames}>
            <span className = '' onClick = {this.onLabelClick}>{label}</span>
            <span>
            <button className='btn btn-outline-success btn-sm' type='button' onClick = {this.onMarkImp}>
                <i className='fa fa-exclamation'></i>
            </button>
            <button className='btn btn-outline-danger btn-sm' type='button'>
                <i className='fa fa-trash-o'></i>
            </button>
            </span>
        </span>
        );
    }

};
