import React, {Component} from 'react'
import AppHeader from '../app-header'
import AppSearch from '../app-search'
import AppList from '../app-list'
import AddPanel from '../add-panel'
import './app.css'

export default class App extends Component {
  
  maxId = 1;

  state = {
     prop : [
      createItem('Drink coffee'),
      createItem('Make React App')
    ]
  };

  createItem = (label) => {
    return {
      label,
      important: false,
      done:false,
      id: this.maxId++
    };
  }

  deleteItem = (id) => {
    this.setState(({prop}) => {
      
      const idx = prop.findIndex((el) => el.id === id);

      const newProp = [...prop.slice(0, idx), ...prop.slice(idx + 1)];

      return {
        prop:newProp
      };

    });
  };

  addItem = (text) => {
    
    const newItem = {
      label: text,
      important: false,
      id: this.maxId++
    };

  
    this.setState(({prop}) => {
      const newArr = [...prop, newItem];

      return {
        prop: newArr
      };

    });

    console.log(text);
  };

  
  
  onToggleDone = (id) => {
    

    

  };

  onToggleImportant = (id) => {
    console.log('ToggleImportant', id);
  };

  render() {
    return (
      <div className='app col-lg-4'>
          <AppHeader/>
          <AppSearch/>
          <AppList prop = {this.state.prop} 
          onDeleted = {this.deleteItem}
          onToggleDone = {this.onToggleDone}
          onToggleImportant = {this.onToggleImportant}
          />
          <AddPanel onPanelAdd = {this.addItem}/>
      </div>    
    );
  }; 
};
