import React, {Component} from 'react'
import AppHeader from '../app-header'
import AppSearch from '../app-search'
import AppList from '../app-list'
import AddPanel from '../add-panel'
import './app.css'

export default class App extends Component {
  
  maxId = 100;

  state = {
     prop : [
      {label: 'Drink coffee', important: false, id: 1},
      {label: 'Make React App', important: true, id: 2}
    ]
  };

  deleteItem = (id) => {
    this.setState(({prop}) => {
      
      const idx = prop.findIndex((el) => el.id === id);

      const newProp = [...prop.slice(0, idx), ...prop.slice(idx + 1)];

      return {
        prop:newProp
      };

    });
  };

  addItem = (text) => {
    
    const newItem = {
      label: text,
      important: false,
      id: maxId++
    };

    this.setState(({prop}) => {
      const newArr = [...prop, newItem];

      return {
        prop: newArr
      };

    });

    console.log(text);
  };

  render() {
    return (
      <div className='app col-lg-4'>
          <AppHeader/>
          <AppSearch/>
          <AppList prop = {this.state.prop} onDeleted = {this.deleteItem}/>
          <AddPanel onPanelAdd = {this.addItem}/>
      </div>    
    );
  };
    
    
  };
