import React, {Component} from 'react'
import './app-list-item.css'

export default  class AppListItem extends Component {

    onLabelClick = () => {
        console.log(`Done: ${this.props.label}`);
    };


    render() {

        const {label, important = false} = this.props;

        const spstyle = {
            color : important ? 'tomato' : 'black',
            
        };

        return (
        <span className='list-group-item app-list-item d-flex justify-content-between success'>
            <span style = {spstyle} onClick = {this.onLabelClick}>{label}</span>
            
            <span>
            <button className='btn btn-outline-success btn-sm' type='button'>
                <i className='fa fa-exclamation'></i>
            </button>
            <button className='btn btn-outline-danger btn-sm' type='button'>
                <i className='fa fa-trash-o'></i>
            </button>
            </span>
        </span>
        );
    }

};
