import React from 'react'
import './app-list-item.css'

const AppListItem = ({label, important = false}) => {
    
    const spstyle = {
        color : important ? 'tomato' : 'black',
        
    };
    
    return (
    <span className='list-group-item app-list-item d-flex justify-content-between'>
        <span  style = {spstyle}>{label}</span>
        <button className='btn btn-outline-success btn-sm' type='button'>
            <i className='fa fa-exclamation'></i>
        </button>
        <button className='btn btn-outline-danger btn-sm' type='button'>
            <i className='fa fa-trash-o'></i>
        </button>
    </span>
    );
}

export default AppListItem;
